<?php
session_start();
require_once("bd/dbconnect.php"); // Connexion à la base de données

// Récupérer tous les partenaires
$queryPartenaires = "SELECT * FROM partenaire";
$resultPartenaires = $conn->query($queryPartenaires);
$partenaires = $resultPartenaires->fetchAll(PDO::FETCH_ASSOC);

// Récupérer tous les utilisateurs
$queryUtilisateurs = "SELECT * FROM utilisateur";
$resultUtilisateurs = $conn->query($queryUtilisateurs);
$utilisateurs = $resultUtilisateurs->fetchAll(PDO::FETCH_ASSOC);

// Récupérer les produits de chaque partenaire
$queryProduits = "SELECT nomProduit, nomPartenaire FROM produit JOIN partenaire ON produit.idPartenaire = partenaire.idPartenaire";
$resultProduits = $conn->query($queryProduits);
$produits = $resultProduits->fetchAll(PDO::FETCH_ASSOC);
?>

